package Dance;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num;
		Boolean isPime;
		num = 12;
		if (num<2);
		isPrime = true;
		for(int i =2; i<=num/i;i++)
		{
			if ((num % i ==0))
			{
				isPrime=false;
				break;
			}
		}
		
       if(isPrime)
       {
    	   System.out.println("number is  Prime Number");
       }
       else 
    	   System.out.println("number is not  Prime Number");   
	}
	

}
